DROP MATERIALIZED VIEW cached_url;

UPDATE cached_path c SET node_id = (SELECT n.id FROM node n WHERE n.public_id = c.public_id);
UPDATE cached_path c SET subject_id = NULL WHERE c.subject_id IS NOT NULL;
UPDATE cached_path c SET topic_id = NULL WHERE c.topic_id IS NOT NULL;

ALTER TABLE resource_resource_type DROP CONSTRAINT resource_resource_type_unique;
ALTER TABLE resource_resource_type DROP CONSTRAINT resource_resource_type_resource_id_fkey;
UPDATE resource_resource_type rrt
    SET resource_id=(
        SELECT n.id FROM node n WHERE n.public_id = (
            SELECT r.public_id FROM resource r WHERE r.id = rrt.resource_id
        )
    );
ALTER TABLE resource_resource_type ADD CONSTRAINT resource_resource_type_unique UNIQUE (resource_id, resource_type_id);
ALTER TABLE resource_resource_type ADD CONSTRAINT resource_resource_type_resource_id_fkey FOREIGN KEY (resource_id) REFERENCES node(id)

UPDATE cached_path cp
SET node_id=(
    SELECT n.id FROM node n WHERE n.public_id = (
        SELECT r.public_id FROM resource r WHERE r.id = cp.resource_id
    )
)
WHERE resource_id IS NOT NULL;
